import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { listJobs } from "../utils/api";
import JobList from "../JobForm/JobList"

export default function JobMenu(){


    const [jobs, setJobs] = useState([]);
    

    useEffect(loadJobs, [])
    function loadJobs(){
      const abortController = new AbortController();
  
      listJobs(abortController.signal)
        .then(setJobs)

      return () => abortController.abort()
      }

      async function convertToBase64URL(file) {
        return new Promise((resolve) => {
          const reader = new FileReader();
    
          reader.onload = () => {
            const base64DataURL = reader.result;
            resolve(base64DataURL);
          };
    
          reader.readAsDataURL(file);
        });
      }  

    return(
        <>
        <div>
          <Link to={"/jobs/new"}><div className="links">New Listing</div></Link>
        </div>
        <div>
          <JobList
          jobs={jobs}
          url={base64URL}
          />
        </div>
        </>
    )
}
