import JobList from "../JobForm/JobList"
import JobEdit from "../JobForm/JobEdit";

export default function JobMenu(){


    const [jobs, setJobs] = useState([]);
    const [base64URL, setBase64URL] = useState("")

    useEffect(loadJobs, [])
    function loadJobs(){
      const abortController = new AbortController();
  
      listJobs(abortController.signal)
        .then(setJobs)

      return () => abortController.abort()
      }

      async function handleChange ({ target }) {
        if(target === "picture"){
            const file = target.files[0]
            const base64DataURL = await convertToBase64URL(file)   
            setBase64URL(base64DataURL);
            
