import React, { useState } from "react";
import JobForm from "./JobForm";
import { createJobs } from "../utils/api"
export const JobNew = () => {
    const initialJobState = {
      neighborhood: "",
      picture: [],
      worker: "",
      date:""
    };
    const [job, setJob] = useState({
        ...initialJobState,
      });
    
    

      async function convertToBase64URL(file) {
        return new Promise((resolve) => {
          const reader = new FileReader();
    
          reader.onload = () => {
            const base64DataURL = reader.result;
            resolve(base64DataURL);
          };
    
          reader.readAsDataURL(file);
        });
      } 



    const handleChange = async ({ target }) => {
      if(target === "picture"){
        const file = target.files[0]
        const base64DataURL = await convertToBase64URL(file)   
        setBase64URL(base64DataURL);
        
    }else{
      setJob({
  
        ...job,
  
        [target.name]: target.value,
  
      });
    }
  
    };

const submitHandler = async (event) => {
    event.preventDefault();
    const abortController = new AbortController()
    
    await createJobs(job, abortController.signal);
    
    console.log("it is working", job)
    setJob(initialJobState)
    return () => abortController.abort();
  };


  return (
    <div>
    <section>
      <h2>New Job:</h2>
      <JobForm
        job={job}
        handleChange={handleChange}
        submitHandler={submitHandler}
      />
    </section>
    </div>
  );
}


