import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { readJobs } from "../utils/api"
import JobForm from "./JobForm";
import { updateJob } from "../utils/api";
import JobRead from "./JobRead";
export default function JobEdit(){
    const initialJobState = {
        neighborhood: "",
        picture: [],
        worker: "",
        date:""
      };
      const [job, setJob] = useState({
          ...initialJobState,
        });
      
      const { job_id } = useParams()

  
        useEffect(() => {
            const abortController = new AbortController()
            readJobs(job_id, abortController.signal)
                .then((readJob) => {
                  setJob()})
            return () => abortController.abort()
        }, [job_id])
  
  
      const handleChange = ({ target }) => {
  
        setJob({
    
          ...job,
    
          [target.name]: target.value,
    
        });
    
      };
  
  const submitHandler = async (event) => {
      event.preventDefault();
      const abortController = new AbortController()
      
      try {
        const updatedJob = await updateJob(job, abortController.signal);

        console.log("Job updated:", updatedJob);

        // Additional handling for the updated job, if needed
    } catch (error) {
        console.error("Error updating job:", error);
    } finally {
        abortController.abort();
    }
    };
    return (
        <>
        <section>
            <h2>Edit Job:</h2>
            <JobForm
            job={job}
            handleChange={handleChange}
            submitHandler={submitHandler}
            setJob={setJob}
            initialJobState={initialJobState}
            />
        </section>
        <section>
            <JobRead 
            job={job}/>
        </section>
        </>
    )
}
