import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { deleteJobs, readJobs } from "../utils/api"
import JobForm from "./JobForm";
import { updateJob } from "../utils/api";
export default function JobEdit(){
    const initialJobState = {
        neighborhood: "",
        picture: [],
        worker: "",
        date:""
      };
      const [job, setJob] = useState({
          ...initialJobState,
        });
      const [jobs, setJobs] = useState([]);
      const { job_id } = useParams()

  
        useEffect(() => {
            const abortController = new AbortController()
            readJobs(job_id, abortController.signal)
                .then(setJob)
            return abortController.abort()
        }, [job_id])
  
  
      const handleChange = ({ target }) => {
  
        setJob({
    
          ...job,
    
          [target.name]: target.value,
    
        });
    
      };
  
  const submitHandler = async (event) => {
      event.preventDefault();
      const abortController = new AbortController()
      
      await updateJob(job, abortController.signal);
      
      console.log("it is working", jobs)
      
      return () => abortController.abort();
    };
    return (
        <>
        <section>
            <h2>Edit Job:</h2>
            <JobForm
            job={job}
            handleChange={handleChange}
            submitHandler={submitHandler}
            setJob={setJob}
            initialJobState={initialJobState}
            />
        </section>
        <section>
            <Jobs 
            job={job}/>
        </section>
        </>
    )
}
