import React from "react";
import { useNavigate } from "react-router-dom"
import hasPermission from "../perm/Permissions";
import { actions } from "../perm/Permissions";


export const JobForm = ({
    job,
    handleChange,
    submitHandler,
  }) => {
    showImage = (e) =>{
      if(e.target.files.lenght > 0){
       this.setState({picture: URL.createObjectURL(e.target.files[0])}) 
      }
      
    }  
  const history = useNavigate()

    return(
      
      
    <form onSubmit={submitHandler} className="formS">
        <fieldset>
        <label htmlforfor="neighborhood">Job Site</label>
        <input 
        id="neighborhood" 
        type="text" 
        name="neighborhood"
        required={true} 
        value={job.neighborhood}
        onChange={handleChange}/>
        

        <label htmlforfor="picture">Proof</label>
        <input
        id="picture"
        type="file"
        name="picture"
        required={true}
        value={job.picture}
        onChange={this.showImage}
        />

        <label htmlforfor="worker">Worker</label>
        <input
        id="worker"
        type="text"
        name="worker"
        required={true}
        maxLength="100" 
        value={job.worker}
        onChange={handleChange}
        />

        <label htmlforfor="date">Date</label>
        <input
        id="date"
        type="date"
        name="date"
        placeholder="YYYY-MM-DD"
        pattern="\d{4}-\d{2}-\d{2}"
        required={true}
        maxLength="100" 
        value={job.date}
        onChange={handleChange}
        />

        </fieldset>
        <button type="submit">submit</button>
        <button type="button" onClick={() => history('/jobs')}>cancel</button>
    </form>
    )
}


export default JobForm
