import React from "react";
import { useHistory } from "react-router-dom";
import hasPermission from "../perm/Permissions";
import { actions } from "../perm/Permissions";


export const JobForm = ({
    job,
    handleChange,
    submitHandler,
    setJob,
    initialJobState
  }) => {
    
      if(hasPermission()){

    return(
      
      
    <form onSubmit={submitHandler} class="formS">
        <fieldset>
        <label htmlforfor="neighborhood">Job Site</label>
        <input 
        id="neighborhood" 
        type="text" 
        name="neighborhood" 
        value={job.neighborhood}
        onChange={handleChange}/>
        

        <label htmlforfor="picture">Proof</label>
        <input
        id="picture"
        type="file"
        name="picture"
        value={job.picture}
        onChange={handleChange}
        />

        <label htmlforfor="worker">Worker</label>
        <input
        id="worker"
        type="text"
        name="worker"
        value={job.worker}
        onChange={handleChange}
        />

        <label htmlforfor="date">Date</label>
        <input
        id="date"
        type="date"
        name="date"
        value={job.date}
        onChange={handleChange}
        />

        </fieldset>
        <button type="submit">submit</button>
        <button type="button" onClick={() => setJob({...initialJobState})}>cancel</button>
    </form>
    )}
}


export default JobForm
