import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { readJobs } from "../utils/api"
import JobForm from "./JobForm";
import { updateJob } from "../utils/api";
import Jobs from "./JobRead";
export default function JobEdit(){
   const { job_id } = useParams() 
   const initialJobState = {
        neighborhood: "",
        picture: [],
        worker: "",
        date:""
      };
      const [job, setJob] = useState({
          ...initialJobState,
        });
      const [base64URL, setBase64URL] = useState("")
      
      
  
        useEffect(() => {
            const abortController = new AbortController()
            //console.log(job_id)
            readJobs(job_id, abortController.signal)
            .then((dataArray) => {
                if (Array.isArray(dataArray) && dataArray.length > 0) {
                    // Assuming the job data is an object within the array
                    const fetchedJob = dataArray[0];
                    //console.log("edit", fetchedJob);
                    setJob(fetchedJob);
            }})
            .catch((error) => {
                    console.error("Error fetching job:", error);
                });

            return () => abortController.abort();
        }, [job_id])
  
        async function convertToBase64URL(file) {
            return new Promise((resolve) => {
              const reader = new FileReader();
        
              reader.onload = () => {
                const base64DataURL = reader.result;
                resolve(base64DataURL);
              };
        
              reader.readAsDataURL(file);
            });
          }  
      
       async function handleChange ({ target }) {
        if(target === "picture"){
            const file = target.files[0]
            const base64DataURL = await convertToBase64URL(file)   
            setBase64URL(base64DataURL);
            
        }else{
            setJob({
                ...job,
                [target.name]: target.value,
        });
        }
        
    
      };
  
  const submitHandler = async (event) => {
      event.preventDefault();
      const abortController = new AbortController()
      
      try {
        //console.log(job)
        await updateJob(job, job_id, abortController.signal);

        console.log("Job updated");

        // Additional handling for the updated job, if needed
    } catch (error) {
        console.error("Error updating job:", error);
    } 
        return () => abortController.abort();
    
    };
    return (
        <>
        <section>
            <h2>Edit Job:</h2>
            <JobForm
            job={job}
            url={base64URL}
            handleChange={handleChange}
            submitHandler={submitHandler}
            />
        </section>
        <section>
            <Jobs
            job={job}
            />
        </section>
        </>
    )
}
